CSVObject.prototype.filter = function (colNo, callback) {
  return this.rows.filter(function(row, rowNo, array) {
    if (row == undefined) return false;
    var value = row[colNo];
    return callback(value, rowNo, array);
  });
};

// sort method
CSVObject.prototype.sort = function (colNo, isAsc) {
  var sort_func;
  if (isAsc == undefined) isAsc = true;
  if (isAsc) {
    sort_func = function (a, b) { return (a[colNo] > b[colNo]) ? 1 : -1; };
  } else {
    sort_func = function (a, b) { return (a[colNo] < b[colNo]) ? 1 : -1; };
  }
  if (this.useHeader) {
    var header = this.rows.shift();
    this.rows.sort(sort_func);
    this.rows.unshift(header);
  } else {
    this.rows.sort(sort_func);
  }
};

CSVObject.prototype.sortNumber = function (colNo, isAsc) {
  var sort_func;
  if (isAsc == undefined) isAsc = true;
  if (isAsc) {
    sort_func = function (a, b) { 
      return parseFloat(a[colNo]) - parseFloat(b[colNo]); 
    };
  } else {
    sort_func = function (a, b) {
      return parseFloat(b[colNo]) - parseFloat(a[colNo]);
    };
  }
  if (this.useHeader) {
    var header = this.rows.shift();
    this.rows.sort(sort_func);
    this.rows.unshift(header);
  } else {
    this.rows.sort(sort_func);
  }
};

// insert / delete
CSVObject.prototype.insertRow = function (rowNo, rowArray) {
  this.rows.splice(rowNo, 0, rowArray);
};
CSVObject.prototype.insert = CSVObject.prototype.insertRow; // alias
CSVObject.prototype.add = function (rowArray) {
  this.rows.push(rowArray);
};
CSVObject.prototype.deleteRow = function (rowNo) {
  return this.rows.splice(rowNo, 1);
};
CSVObject.prototype.insertCol = function (colNo, values) {
  this.rows.map(function(row, index, array) {
    row.splice(colNo, 0, values[index]);
    return row;
  });
};
CSVObject.prototype.deleteCol = function (colNo) {
  this.rows.map(function(row, index, array) {
    row.splice(colNo, 1);
    return row;
  });
};

Object.defineProperty(CSVObject.prototype, 'length', {
  get: function () {
    if (this.rows == undefined) return 0;
    return this.rows.length;
  }
});

// exports
CSV.CSVObject = CSVObject;
module.exports = CSV;

// csv-lite.js --- for Browser
'use strict';
var options = {
  delimiter: ',',
  eol: "\r\n"
};

function parse(txt, delimiter) {
  // delimiter
  if (delimiter == undefined) {
    delimiter = options.delimiter;
  }
  // check txt
  txt = "" + txt + "\n";
  // convert CRLF to LF, and CR to LF
  txt = txt.replace(/(\r\n|\r)/g, "\n");
  // trim right
  txt = txt.replace(/\s+$/, '')+"\n";
  // set pattern
  var patToDelim = "^(.*?)([\\"+delimiter+"\\n])";
  var reToDelim = new RegExp(patToDelim);
  // if value is number then convert to float
  var convType = function (v) {
    if (typeof(v) == 'string') {
      if (v.search(/^[0-9\.]+$/) >= 0) {
        v = parseFloat(v); // convert number
      }
    }
    return v;
  };
  // parse txt
  var res = [], cells = [], c = ''
  while (txt != "") {
    // first check delimiter (because /^\s+/ skip delimiter'\t') (#3)
    c = txt.charAt(0);
    if (c === delimiter) {
      txt = txt.substr(1)
      cells.push('');
      continue;
    }
    // second check LF (#7)
    if (c === "\n") {
      cells.push('');
      res.push(cells);
      cells = [];
      txt = txt.substr(1);
      continue;
    }

    // trim white space
    txt = txt.replace(/^\s+/, "");
    c = txt.charAt(0);

    // no data
    if (c === delimiter) {
      console.log("delimiter");
      cells.push('');
      txt = txt.substr(delimiter.length);
      continue;
    }

    // written using the dialect of Excel
    if (c === '=' && txt.charAt(1) === '"') {
      txt = txt.substr(1);
      continue;
    }

    // number or simple string
    if (c !== '"') { // number or simple str
      var m = reToDelim.exec(txt);
      if (!m) {
        cells.push(convType(txt));
        res.push(cells);
        cells = [];
        break;
      }
      if (m[2] == "\n") {
        cells.push(convType(m[1]));
        res.push(cells);
        cells = [];
      } else if (m[2] == delimiter) {
        cells.push(convType(m[1]));
      }
      txt = txt.substr(m[0].length);
      continue;
    }
    // "" ... blank data
    if (txt.substr(0, 2) == '""') {
      cells.push('');
      txt = txt.substr(2);
      continue;
    }
    // "..."
    var i = 1, s = "";
    while (i < txt.length) {
      var c1 = txt.charAt(i);
      var c2 = txt.charAt(i + 1);
      // console.log("@" + c1 + c2);
      // 2quote => 1quote char
      if (c1 == '"' && c2 == '"') {
        i += 2;
        s += '"';
        continue;
      }
      if (c1 == '"') {
        i++;
        if (c2 == delimiter) {
          i++;
          cells.push(convType(s));
          s = '';
          break;
        }
        if (c2 == "\n") {
          i++;
          cells.push(convType(s));
          res.push(cells);
          cells = [];
          break;
        }
        // if (c2 == " " || c2 == "\t") {
        i++;
        continue;
      }
      s += c1;
      i++;
    }
    txt = txt.substr(i);
  }
  if (cells.length > 0) res.push(cells);
  return res;
}

function stringify(ary, delimiter, eol) {
  // check arguments
  if (delimiter == undefined) {
    delimiter = options.delimiter;
  }
  if (eol == undefined) {
    eol = options.eol;
  }
  var valueConv = genValueConverter(delimiter, eol);
  if (ary == undefined) return "";
  var r = "";
  for (var i = 0; i < ary.length; i++) {
    var cells = ary[i];
    if (cells == undefined) {
      r += eol; continue;
    }
    for (var j = 0; j < cells.length; j++) {
      cells[j] = valueConv(cells[j]);
    }
    r += cells.join(delimiter) + eol;
  }
  // replace return code
  r = r.replace(/(\r\n|\r|\n)/g, eol)
  return r;
}

function genValueConverter(delimiter, eol) {
  return (function(s) {
    s = "" + s;
    var f_quot = false;
    if (s.indexOf("\n") >= 0 || s.indexOf("\r") >= 0) f_quot = true;
    if (s.indexOf(delimiter) >= 0) f_quot = true;
    if (s.indexOf('"') >= 0) {
      f_quot = true;
      s = s.replace(/\"/g, '""');
    }
    if (f_quot) s = '"' + s + '"';
    return s;
  });
}

// exports
module.exports = {
  "options"       : options,
  "parse"         : parse,
  "stringify"     : stringify
};

var jsReleases = require('node-releases/data/processed/envs.json')
var agents = require('caniuse-lite/dist/unpacker/agents').agents
var jsEOL = require('node-releases/data/release-schedule/release-schedule.json')
var path = require('path')
var e2c = require('electron-to-chromium/versions')

var BrowserslistError = require('./error')
var env = require('./node') // Will load browser.js in webpack

var YEAR = 365.259641 * 24 * 60 * 60 * 1000
var ANDROID_EVERGREEN_FIRST = 37

var QUERY_OR = 1
var QUERY_AND = 2

function isVersionsMatch (versionA, versionB) {
  return (versionA + '.').indexOf(versionB + '.') === 0
}

function isEolReleased (name) {
  var version = name.slice(1)
  return jsReleases.some(function (i) {
    return isVersionsMatch(i.version, version)
  })
}

function normalize (versions) {
  return versions.filter(function (version) {
    return typeof version === 'string'
  })
}

function normalizeElectron (version) {
  var versionToUse = version
  if (version.split('.').length === 3) {
    versionToUse = version
      .split('.')
      .slice(0, -1)
      .join('.')
  }
  return versionToUse
}

function nameMapper (name) {
  return function mapName (version) {
    return name + ' ' + version
  }
}

function getMajor (version) {
  return parseInt(version.split('.')[0])
}

function getMajorVersions (released, number) {
  if (released.length === 0) return []
  var majorVersions = uniq(released.map(getMajor))
  var minimum = majorVersions[majorVersions.length - number]
  if (!minimum) {
    return released
  }
  var selected = []
  for (var i = released.length - 1; i >= 0; i--) {
    if (minimum > getMajor(released[i])) break
    selected.unshift(released[i])
  }
  return selected
}

function uniq (array) {
  var filtered = []
  for (var i = 0; i < array.length; i++) {
    if (filtered.indexOf(array[i]) === -1) filtered.push(array[i])
  }
  return filtered
}

// Helpers

function fillUsage (result, name, data) {
  for (var i in data) {
    result[name + ' ' + i] = data[i]
  }
}

function generateFilter (sign, version) {
  version = parseFloat(version)
  if (sign === '>') {
    return function (v) {
      return parseFloat(v) > version
    }
  } else if (sign === '>=') {
    return function (v) {
      return parseFloat(v) >= version
    }
  } else if (sign === '<') {
    return function (v) {
      return parseFloat(v) < version
    }
  } else {
    return function (v) {
      return parseFloat(v) <= version
    }
  }
}

function generateSemverFilter (sign, version) {
  version = version.split('.').map(parseSimpleInt)
  version[1] = version[1] || 0
  version[2] = version[2] || 0
  if (sign === '>') {
    return function (v) {
      v = v.split('.').map(parseSimpleInt)
      return compareSemver(v, version) > 0
    }
  } else if (sign === '>=') {
    return function (v) {
      v = v.split('.').map(parseSimpleInt)
      return compareSemver(v, version) >= 0
    }
  } else if (sign === '<') {
    return function (v) {
      v = v.split('.').map(parseSimpleInt)
      return compareSemver(version, v) > 0
    }
  } else {
    return function (v) {
      v = v.split('.').map(parseSimpleInt)
      return compareSemver(version, v) >= 0
    }
  }
}

function parseSimpleInt (x) {
  return parseInt(x)
}

function compare (a, b) {
  if (a < b) return -1
  if (a > b) return +1
  return 0
}

function compareSemver (a, b) {
  return (
    compare(parseInt(a[0]), parseInt(b[0])) ||
    compare(parseInt(a[1] || '0'), parseInt(b[1] || '0')) ||
    compare(parseInt(a[2] || '0'), parseInt(b[2] || '0'))
  )
}

// this follows the npm-like semver behavior
function semverFilterLoose (operator, range) {
  range = range.split('.').map(parseSimpleInt)
  if (typeof range[1] === 'undefined') {
    range[1] = 'x'
  }
  // ignore any patch version because we only return minor versions
  // range[2] = 'x'
  switch (operator) {
    case '<=':
      return function (version) {
        version = version.split('.').map(parseSimpleInt)
        return compareSemverLoose(version, range) <= 0
      }
    default:
    case '>=':
      return function (version) {
        version = version.split('.').map(parseSimpleInt)
        return compareSemverLoose(version, range) >= 0
      }
  }
}

// this follows the npm-like semver behavior
function compareSemverLoose (version, range) {
  if (version[0] !== range[0]) {
    return version[0] < range[0] ? -1 : +1
  }
  if (range[1] === 'x') {
    return 0
  }
  if (version[1] !== range[1]) {
    return version[1] < range[1] ? -1 : +1
  }
  return 0
}

function resolveVersion (data, version) {
  if (data.versions.indexOf(version) !== -1) {
    return version
  } else if (browserslist.versionAliases[data.name][version]) {
    return browserslist.versionAliases[data.name][version]
  } else {
    return false
  }
}

function normalizeVersion (data, version) {
  var resolved = resolveVersion(data, version)
  if (resolved) {
    return resolved
  } else if (data.versions.length === 1) {
    return data.versions[0]
  } else {
    return false
  }
}

function filterByYear (since, context) {
  since = since / 1000
  return Object.keys(agents).reduce(function (selected, name) {
    var data = byName(name, context)
    if (!data) return selected
    var versions = Object.keys(data.releaseDate).filter(function (v) {
      return data.releaseDate[v] >= since
    })
    return selected.concat(versions.map(nameMapper(data.name)))
  }, [])
}

function cloneData (data) {
  return {
    name: data.name,
    versions: data.versions,
    released: data.released,
    releaseDate: data.releaseDate
  }
}

function mapVersions (data, map) {
  data.versions = data.versions.map(function (i) {
    return map[i] || i
  })
  data.released = data.versions.map(function (i) {
    return map[i] || i
  })
  var fixedDate = { }
  for (var i in data.releaseDate) {
    fixedDate[map[i] || i] = data.releaseDate[i]
  }
  data.releaseDate = fixedDate
  return data
}

function byName (name, context) {
  name = name.toLowerCase()
  name = browserslist.aliases[name] || name
  if (context.mobileToDesktop && browserslist.desktopNames[name]) {
    var desktop = browserslist.data[browserslist.desktopNames[name]]
    if (name === 'android') {
      return normalizeAndroidData(cloneData(browserslist.data[name]), desktop)
    } else {
      var cloned = cloneData(desktop)
      cloned.name = name
      if (name === 'op_mob') {
        cloned = mapVersions(cloned, { '10.0-10.1': '10' })
      }
      return cloned
    }
  }
  return browserslist.data[name]
}

function normalizeAndroidVersions (androidVersions, chromeVersions) {
  var firstEvergreen = ANDROID_EVERGREEN_FIRST
  var last = chromeVersions[chromeVersions.length - 1]
  return androidVersions
    .filter(function (version) { return /^(?:[2-4]\.|[34]$)/.test(version) })
    .concat(chromeVersions.slice(firstEvergreen - last - 1))
}

function normalizeAndroidData (android, chrome) {
  android.released = normalizeAndroidVersions(android.released, chrome.released)
  android.versions = normalizeAndroidVersions(android.versions, chrome.versions)
  return android
}

function checkName (name, context) {
  var data = byName(name, context)
  if (!data) throw new BrowserslistError('Unknown browser ' + name)
  return data
}

function unknownQuery (query) {
  return new BrowserslistError(
    'Unknown browser query `' + query + '`. ' +
    'Maybe you are using old Browserslist or made typo in query.'
  )
}

function filterAndroid (list, versions, context) {
  if (context.mobileToDesktop) return list
  var released = browserslist.data.android.released
  var last = released[released.length - 1]
  var diff = last - ANDROID_EVERGREEN_FIRST - versions
  if (diff > 0) {
    return list.slice(-1)
  } else {
    return list.slice(diff - 1)
  }
}

/**
 * Resolves queries into a browser list.
 * @param {string|string[]} queries Queries to combine.
 * Either an array of queries or a long string of queries.
 * @param {object} [context] Optional arguments to
 * the select function in `queries`.
 * @returns {string[]} A list of browsers
 */
function resolve (queries, context) {
  if (Array.isArray(queries)) {
    queries = flatten(queries.map(parse))
  } else {
    queries = parse(queries)
  }

  return queries.reduce(function (result, query, index) {
    var selection = query.queryString

    var isExclude = selection.indexOf('not ') === 0
    if (isExclude) {
      if (index === 0) {
        throw new BrowserslistError(
          'Write any browsers query (for instance, `defaults`) ' +
          'before `' + selection + '`')
      }
      selection = selection.slice(4)
    }

    for (var i = 0; i < QUERIES.length; i++) {
      var type = QUERIES[i]
      var match = selection.match(type.regexp)
      if (match) {
        var args = [context].concat(match.slice(1))
        var array = type.select.apply(browserslist, args).map(function (j) {
          var parts = j.split(' ')
          if (parts[1] === '0') {
            return parts[0] + ' ' + byName(parts[0], context).versions[0]
          } else {
            return j
          }
        })

        switch (query.type) {
          case QUERY_AND:
            if (isExclude) {
              return result.filter(function (j) {
                return array.indexOf(j) === -1
              })
            } else {
              return result.filter(function (j) {
                return array.indexOf(j) !== -1
              })
            }
          case QUERY_OR:
          default:
            if (isExclude) {
              var filter = { }
              array.forEach(function (j) {
                filter[j] = true
              })
              return result.filter(function (j) {
                return !filter[j]
              })
            }
            return result.concat(array)
        }
      }
    }

    throw unknownQuery(selection)
  }, [])
}

var cache = { }

/**
 * Return array of browsers by selection queries.
 *
 * @param {(string|string[])} [queries=browserslist.defaults] Browser queries.
 * @param {object} [opts] Options.
 * @param {string} [opts.path="."] Path to processed file.
 *                                 It will be used to find config files.
 * @param {string} [opts.env="production"] Processing environment.
 *                                         It will be used to take right
 *                                         queries from config file.
 * @param {string} [opts.config] Path to config file with queries.
 * @param {object} [opts.stats] Custom browser usage statistics
 *                              for "> 1% in my stats" query.
 * @param {boolean} [opts.ignoreUnknownVersions=false] Do not throw on unknown
 *                                                     version in direct query.
 * @param {boolean} [opts.dangerousExtend] Disable security checks
 *                                         for extend query.
 * @param {boolean} [opts.mobileToDesktop] Alias mobile browsers to the desktop
 *                                         version when Can I Use doesn't have
 *                                         data about the specified version.
 * @returns {string[]} Array with browser names in Can I Use.
 *
 * @example
 * browserslist('IE >= 10, IE 8') //=> ['ie 11', 'ie 10', 'ie 8']
 */
function browserslist (queries, opts) {
  if (typeof opts === 'undefined') opts = { }

  if (typeof opts.path === 'undefined') {
    opts.path = path.resolve ? path.resolve('.') : '.'
  }

  if (typeof queries === 'undefined' || queries === null) {
    var config = browserslist.loadConfig(opts)
    if (config) {
      queries = config
    } else {
      queries = browserslist.defaults
    }
  }

  if (!(typeof queries === 'string' || Array.isArray(queries))) {
    throw new BrowserslistError(
      'Browser queries must be an array or string. Got ' + typeof queries + '.')
  }

  var context = {
    ignoreUnknownVersions: opts.ignoreUnknownVersions,
    dangerousExtend: opts.dangerousExtend,
    mobileToDesktop: opts.mobileToDesktop
  }

  env.oldDataWarning(browserslist.data)
  var stats = env.getStat(opts, browserslist.data)
  if (stats) {
    context.customUsage = { }
    for (var browser in stats) {
      fillUsage(context.customUsage, browser, stats[browser])
    }
  }

  var cacheKey = JSON.stringify([queries, context])
  if (cache[cacheKey]) return cache[cacheKey]

  var result = uniq(resolve(queries, context)).sort(function (name1, name2) {
    name1 = name1.split(' ')
    name2 = name2.split(' ')
    if (name1[0] === name2[0]) {
      // assumptions on caniuse data
      // 1) version ranges never overlaps
      // 2) if version is not a range, it never contains `-`
      var version1 = name1[1].split('-')[0]
      var version2 = name2[1].split('-')[0]
      return compareSemver(version2.split('.'), version1.split('.'))
    } else {
      return compare(name1[0], name2[0])
    }
  })
  if (!process.env.BROWSERSLIST_DISABLE_CACHE) {
    cache[cacheKey] = result
  }
  return result
}

function parse (queries) {
  var qs = []
  do {
    queries = doMatch(queries, qs)
  } while (queries)
  return qs
}

function doMatch (string, qs) {
  var or = /^(?:,\s*|\s+or\s+)(.*)/i
  var and = /^\s+and\s+(.*)/i

  return find(string, function (parsed, n, max) {
    if (and.test(parsed)) {
      qs.unshift({ type: QUERY_AND, queryString: parsed.match(and)[1] })
      return true
    } else if (or.test(parsed)) {
      qs.unshift({ type: QUERY_OR, queryString: parsed.match(or)[1] })
      return true
    } else if (n === max) {
      qs.unshift({ type: QUERY_OR, queryString: parsed.trim() })
      return true
    }
    return false
  })
}

function find (string, predicate) {
  for (var n = 1, max = string.length; n <= max; n++) {
    var parsed = string.substr(-n, n)
    if (predicate(parsed, n, max)) {
      return string.slice(0, -n)
    }
  }
  return ''
}

function flatten (array) {
  if (!Array.isArray(array)) return [array]
  return array.reduce(function (a, b) {
    return a.concat(flatten(b))
  }, [])
}

