          me.list = [];

          if (me.canvas !== null) {
            me.ctx.clearRect(0, 0, me.canvas.width, me.canvas.height);
          }

          me.target = -1;
          me.flagSetTimer = false;
        },
        drawTurtle: function drawTurtle(id) {
          var tt = this.list[id];
          var cr = this.canvas_r; // カメの位置を移動

          tt.canvas.style.left = cr.left + tt.x - tt.cx + 'px';
          tt.canvas.style.top = cr.top + tt.y - tt.cx + 'px';

          if (!tt.f_update) {
            return;
          }

          if (!tt.flagLoaded) {
            return;
          }

          tt.f_update = false;
          tt.ctx.clearRect(0, 0, tt.canvas.width, tt.canvas.height);

